	'section'  => 'pure_woo_single',
) );

Kirki::add_field( 'pure', array(
	'type'            => 'text',
	'settings'        => 'buying_guide_link',
	'label'           => esc_attr__( 'Purchase help link', 'pure' ),
	'default'         => pure_get_default_option( 'buying_guide_link' ),
	'section'         => 'pure_woo_single',
) );

Kirki::add_field( 'pure', array(
	'type'            => 'editor',
	'settings'        => 'wc_single_promotion',
	'label'           => esc_attr__( 'Promotion', 'pure' ),
	'section'         => 'pure_woo_single',
) );

function pure_cz_add_to_cart_text() {

	echo pure_get_option( 'add_to_cart_text' );
}

